function setupSkyLabels(){$(document).on("focus",".field-wrapper",function(){var a=$(this);addOrRemoveHasTextClass(a),a.addClass("js-slider-focused")}),$(document).on("blur",".field-wrapper",function(){var a=$(this);addOrRemoveHasTextClass(a),a.removeClass("js-slider-focused")})}function addOrRemoveHasTextClass(a){fieldWrapperHasText(a)?a.addClass("js-slider-has-text"):a.removeClass("js-slider-has-text")}function fieldWrapperHasText(a){var b=findTextFieldInside(a);return $.trim(b.val()).length}function findTextFieldInside(a){var b=a.find("input");return b.length||(b=a.find("textarea")),b}